CREATE TABLE IF NOT EXISTS `bahan_baku` (
  `id` INT NOT NULL PRIMARY KEY,
  `nama` VARCHAR(255) NOT NULL,
  `satuan` VARCHAR(50),
  `stok_level` DECIMAL(10,2) DEFAULT 0.00,
  `harga` DECIMAL(12,2) DEFAULT 0.00,
